mapper.update:
update t_advert_banner
set IS_DEL=1
where ID =#{advId}

update t_advert_banner
set IS_DEL=0

    ,ADVERT_TITLE=#{advTitle}


    ,IMG_URL=#{advImg}

where ID=#{advId}

START TRANSACTION;
UPDATE t_advert_banner SET SORT_TIME =#{laterSortTime} WHERE ID = #{formerAdvId};
UPDATE t_advert_banner SET SORT_TIME =#{formerSortTime} WHERE ID = #{laterAdvId};
COMMIT;
