mapper.insert:
INSERT INTO t_navigation_top(
NAVIGATION_ID,
POST_ID,
SORT_TIME,
CREATE_TIME
)VALUES(#{navigationId},#{postId},NOW(),NOW());

mapper.update:
UPDATE t_navigation_top
SET POST_ID = #{postId}
WHERE ID =#{topPostId}

mapper.select:
SELECT a.NAVIGATION_ID navigationId, b.Title navigationTitle, a.POST_ID postId, a.ID topPostId, a.CREATE_TIME createTime FROM t_navigation_top a,t_navigation b WHERE a.IS_DEL=0 AND a.NAVIGATION_ID = b.ID
AND a.NAVIGATION_ID = #{navigationId}
AND a.POST_ID = #{postId}
