mapper.insert:
BEGIN;
INSERT INTO t_navigation_top(
NAVIGATION_ID,
POST_ID,
SORT_TIME,
CREATE_TIME
)VALUES(#{navigationId},#{postId},NOW(),NOW());
UPDATE t_post_base
SET IS_TOP = 1
WHERE
ID = #{postId};
UPDATE t_navigation
SET MAX_TOP_NUM = (SELECT COUNT(0) FROM t_navigation_top WHERE NAVIGATION_ID =#{navigationId} AND IS_DEL = 0)
WHERE ID = #{navigationId};
COMMIT;

mapper.update:
START TRANSACTION;
UPDATE t_navigation_top SET SORT_TIME =#{laterSortTime} WHERE ID = #{formerNavId};
UPDATE t_navigation_top SET SORT_TIME =#{formerSortTime} WHERE ID = #{laterNavId};
COMMIT;

UPDATE t_navigation_top
SET POST_ID = #{postId}
WHERE ID =#{topPostId}

BEGIN;
UPDATE t_navigation_top
SET IS_DEL = 1
WHERE ID =#{topPostId};
UPDATE t_navigation
SET MAX_TOP_NUM = (SELECT COUNT(0) FROM t_navigation_top WHERE NAVIGATION_ID =#{navigationId} AND IS_DEL = 0)
WHERE ID = #{navigationId};
COMMIT;

mapper.select:
SELECT a.NAVIGATION_ID navigationId, b.Title navigationTitle, a.POST_ID postId, a.ID topPostId, a.SORT_TIME sortTime FROM t_navigation_top a,t_navigation b WHERE a.IS_DEL=0 AND a.NAVIGATION_ID = b.ID
AND a.NAVIGATION_ID = #{navigationId}
ORDER BY a.SORT_TIME DESC
