mapper.insert:
BEGIN;
INSERT INTO t_navigation_top(
NAVIGATION_ID,
POST_ID,
SORT_TIME,
CREATE_TIME
)VALUES(#{navigationId},#{postId},NOW(),NOW());
UPDATE t_navigation
SET MAX_TOP_NUM =(SELECT COUNT(0) FROM t_navigation_top WHERE NAVIGATION_ID =#{navigationId})
WHERE ID = #{navigationId};
COMMIT;

mapper.select:
SELECT a.NAVIGATION_ID navigationId, a.POST_ID postId, a.ID topPostId, a.CREATE_TIME createTime FROM t_navigation_top a WHERE IS_DEL=0
AND NAVIGATION_ID = #{navigationId}
AND POST_ID = #{postId}
