mapper.select:
select count(0) from (SELECT u.WX_NICK_NAME poster, u.WX_GENDER posterGender, u.WX_AVATAR_URL posterAvatar, f.TYPE_NAME typeName, m.ID postId, m.CREATE_TIME createTime, m.ADDRESS address, m.LABELS postLabels, m.CONTENT content, m.MIN_PRICE minPrice, m.MAX_PRICE maxPrice, m.PHONE phone, m.TOTAL_BROWSE browse, m.TOTAL_COMMENTS comments, m.TOTAL_RESONATE likes, m.DEV_BROWSE devBrowse, m.DEV_RESONATE devLike, m.TOTAL_COLLECTION collection, m.FEE fee, m.ACTIVE_TIME activeTime, (SELECT IFNULL((SELECT id FROM t_user_follow u WHERE u.FAN_ID =#{userId} AND u.FOLLOW_ID=m.USER_ID),'0') ) isFollow FROM t_wx_user u, t_post_base m, t_post_type f WHERE m.TYPE_ID = f.ID AND u.USER_ID = m.USER_ID
AND m.IS_DEL = #{isDel}
AND m.TYPE_ID = #{typeId}
AND m.IS_SUB = #{isSub}
AND m.IS_MALL = #{isMall}
AND m.ID = #{postId}
AND u.WX_NICK_NAME LIKE CONCAT(CONCAT('%',#{nickname}),'%')
AND (m.CONTENT LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR m.ADDRESS LIKE CONCAT(CONCAT('%',#{keyword}),'%'))
AND m.CREATE_TIME BETWEEN DATE_FORMAT(#{startTime},'%Y-%m-%d %T') AND DATE_FORMAT(#{endTime},'%Y-%m-%d %T')
ORDER BY ACTIVE_TIME DESC) g

SELECT u.WX_NICK_NAME poster, u.WX_GENDER posterGender, u.WX_AVATAR_URL posterAvatar, f.TYPE_NAME typeName, m.ID postId, m.CREATE_TIME createTime, m.ADDRESS address, m.LABELS postLabels, m.CONTENT content, m.MIN_PRICE minPrice, m.MAX_PRICE maxPrice, m.PHONE phone, m.TOTAL_BROWSE browse, m.TOTAL_COMMENTS comments, m.TOTAL_RESONATE likes, m.DEV_BROWSE devBrowse, m.DEV_RESONATE devLike, m.TOTAL_COLLECTION collection, m.FEE fee, m.ACTIVE_TIME activeTime, m.IS_LOWER_SHELF isLowerShelf, m.IS_MALL isMall, m.IS_SUB isSub, (SELECT IFNULL((SELECT id FROM t_user_follow u WHERE u.FAN_ID =#{userId} AND u.FOLLOW_ID=m.USER_ID),'0') ) isFollow FROM t_wx_user u, t_post_base m, t_post_type f WHERE m.TYPE_ID = f.ID AND u.USER_ID = m.USER_ID
AND m.IS_DEL = #{isDel}
AND m.TYPE_ID = #{typeId}
AND m.IS_SUB = #{isSub}
AND m.IS_MALL = #{isMall}
AND m.ID = #{postId}
AND u.WX_NICK_NAME LIKE CONCAT(CONCAT('%',#{nickname}),'%')
AND (m.CONTENT LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR m.ADDRESS LIKE CONCAT(CONCAT('%',#{keyword}),'%'))
AND m.CREATE_TIME BETWEEN DATE_FORMAT(#{startTime},'%Y-%m-%d %T') AND DATE_FORMAT(#{endTime},'%Y-%m-%d %T')
ORDER BY m.CREATE_TIME DESC
ORDER BY activeTime DESC
,browse DESC

mapper.update:
UPDATE t_post_base
SET IS_DEL=0

    ,DEV_BROWSE = #{devBrowse}


    ,DEV_RESONATE =#{devLike}

WHERE ID = #{postId}

mapper.select:
SELECT u.USER_ID postUserId, u.WX_NICK_NAME poster, u.WX_GENDER posterGender, u.WX_AVATAR_URL posterAvatar, f.TYPE_NAME typeName, m.ID postId, m.CREATE_TIME createTime, m.ADDRESS address, m.LABELS postLabels, m.CONTENT content, m.MIN_PRICE minPrice, m.MAX_PRICE maxPrice, m.PHONE phone, m.TOTAL_COMMENTS comments, m.TOTAL_BROWSE+m.DEV_BROWSE browse, m.TOTAL_RESONATE+m.DEV_RESONATE likes, m.TOTAL_COLLECTION collection, m.FEE fee, m.ACTIVE_TIME activeTime, m.IS_MALL isMall, m.IS_SUB isSub, (SELECT IFNULL((SELECT id FROM t_user_follow u WHERE u.FAN_ID =#{userId} AND u.FOLLOW_ID=m.USER_ID),'0') ) isFollow FROM t_wx_user u, t_post_base m, t_post_type f WHERE m.TYPE_ID = f.ID AND u.USER_ID = m.USER_ID AND m.IS_LOWER_SHELF = '0' AND m.IS_DEL = '0'
AND m.TYPE_ID = #{typeId}
AND m.ID = #{postId}
AND m.IS_SUB = #{isSub}
AND m.IS_MALL = #{isMall}
AND u.WX_NICK_NAME LIKE CONCAT(CONCAT('%',#{nickname}),'%')
AND (m.CONTENT LIKE CONCAT(CONCAT('%',#{keyword}),'%') OR m.ADDRESS LIKE CONCAT(CONCAT('%',#{keyword}),'%'))
ORDER BY m.CREATE_TIME DESC
ORDER BY activeTime DESC
,browse DESC
LIMIT ${start},${end}

mapper.update:
UPDATE t_post_base
SET IS_DEL=0,IS_LOWER_SHELF=#{isLowerShelf}
WHERE ID = #{postId}

BEGIN;
UPDATE t_post_base
SET IS_DEL = 1
WHERE ID =#{postId};
UPDATE t_comment
SET IS_DEL = 1
WHERE
POST_ID = #{postId};
COMMIT;

UPDATE t_post_base
SET IS_DEL = 0
WHERE ID =#{postId}
