mapper.select:
SELECT COUNT(0) FROM ( SELECT a.ID postLabelId, a.PARENT_ID postParentId, a.CONTENT labelContent FROM t_post_label a WHERE a.IS_DEL = 0
AND a.PARENT_ID = #{labelParentId}
ORDER BY postParentId ) g

SELECT a.ID postLabelId, a.PARENT_ID labelParentId, a.CONTENT labelContent, a.TYPE labelTypeId, a.TYPE_IMG_URL labelImg FROM t_post_label a WHERE a.IS_DEL = 0
AND a.PARENT_ID !=0
AND a.PARENT_ID = #{labelParentId}
AND a.TYPE = #{labelTypeId}
ORDER BY labelParentId

mapper.update:
UPDATE t_post_label a
SET a.CONTENT = #{content}
WHERE a.ID = #{postLabelId}

update t_post_label
SET IS_DEL = 1
WHERE ID = #{postLabelId}
