mapper.insert:
    INSERT t_post_type (
        TYPE_IMTG_URL,
        TYPE_NAME,
        CREATE_TIME
    )
    VALUES(
        #{postTypeImg},
        #{postTypeName},
        #{postTypeCreateTime}
		)

mapper.update:
UPDATE t_post_type a
SET a.IS_DEL = '0'

    ,a.TYPE_NAME = #{postTypeName}


    ,a.TYPE_IMTG_URL = #{postTypeImg}


    ,a.CREATE_TIME = DATE_FORMAT(#{postTypeCreateTime},'%Y.%m.%d %T')

WHERE
a.ID = #{postTypeId}
