mapper.select:
SELECT t.TITLE sysTempTitle, t.CONTENT sysTempContent, t.ID sysTempId, t.IS_GUIDE isGuide FROM t_sys_template t WHERE t.IS_DEL = 0
AND t.TITLE like CONCAT(#{title},'%')
ORDER BY t.IS_GUIDE DESC ,t.ID DESC LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE t_sys_template


        TITLE =#{sysTempTitle},


        CONTENT =#{sysTempContent},


        IS_GUIDE =#{isGuide}


WHERE ID = #{sysTempId}

UPDATE t_sys_template
SET IS_DEL = 1
WHERE ID = #{sysTempId}
