mapper.update:
UPDATE t_user_browse
SET CREATE_TIME = NOW()
WHERE USER_ID = #{userId} AND POST_ID = #{postId};

mapper.insert:
BEGIN;
INSERT INTO t_user_browse(
USER_ID,
POST_ID,
CREATE_TIME
)VALUES (#{userId},#{postId},NOW());
UPDATE t_post_base
SET TOTAL_BROWSE = (SELECT COUNT(0) FROM t_user_browse a WHERE a.POST_ID = #{postId}) WHERE ID = #{postId};
COMMIT;
