mapper.select:
SELECT count(0) from ( SELECT u.WX_AVATAR_URL likeImg, u.WX_NICK_NAME likeNickname, a.USER_ID userId, a.CREATE_TIME createTime FROM t_wx_user u, t_user_resonate a WHERE a.TYPE ='1' AND a.TARGET_ID = #{postId} AND u.USER_ID = a.USER_ID
limit #{limit}
) g

mapper.insert:
BEGIN;
INSERT INTO t_user_resonate (
    TYPE,
    TARGET_ID,
    USER_ID,
    CREATE_TIME
)
VALUES
    (
        '1',
        #{postId},
        #{userId},
        DATE_FORMAT(#{postLikeCreateTime},'%Y.%m.%d %T')
    );
UPDATE t_post_base
SET TOTAL_RESONATE = (select count(0) from t_user_resonate where TARGET_ID=#{postId}), ACTIVE_TIME = NOW()
WHERE
    ID = #{postId};
COMMIT;

mapper.update:
BEGIN;
UPDATE t_user_resonate
SET IS_DEL =
IF (
    t_user_resonate.IS_DEL = '1',
    '0',
    '1'
)
WHERE
    t_user_resonate.USER_ID =#{userId} AND t_user_resonate.TARGET_ID=#{postId};
UPDATE t_post_base
SET
    ACTIVE_TIME = NOW()
WHERE
    ID = #{postId};
COMMIT;

mapper.select:
SELECT ID
FROM t_user_resonate
WHERE USER_ID = #{userId}
  AND TARGET_ID = #{postId}
  AND IS_DEL = 0
