mapper.update:
UPDATE t_wx_user
SET SUMMARY =#{summary}
WHERE USER_ID = #{userId}

UPDATE t_wx_user
SET IS_DEL = 0

    ,DEV_FANS =#{devFans}

where
    USER_ID =#{userId}

mapper.select:
SELECT count(0) FROM t_wx_user
t_wx_user.WX_NICK_NAME LIKE CONCAT(CONCAT('%',#{nickname}),'%')
and date(t_wx_user.UPDATE_TIME) BETWEEN #{startTime} and #{endTime}

SELECT USER_ID wxUserId, OPEN_ID openId, UNION_ID unionId, WX_AVATAR_URL wxAvatar, WX_NICK_NAME wxNickname, WX_GENDER wxGender, WX_CITY wxCity, WX_PROVINCE wxProvince, WX_COUNTRY wxCountry, DEV_FANS devFans, DATE_FORMAT(CREATE_TIME, '%Y-%m-%d %T') createTime, DATE_FORMAT(UPDATE_TIME, '%Y-%m-%d %T') updateTime, (SELECT COUNT(0)FROM t_user_follow a WHERE a.FOLLOW_ID =#{userId} and IS_DEL =0) fans, (SELECT COUNT(0)FROM t_user_follow a WHERE a.FAN_ID =#{userId} AND IS_DEL =0) followers, (SELECT COUNT(0) FROM t_post_base WHERE USER_ID =#{userId} AND IS_DEL =0) releaseNum FROM t_wx_user
IS_DEL='0'
AND t_wx_user.USER_ID =#{userId}
AND t_wx_user.WX_NICK_NAME LIKE CONCAT(CONCAT('%',#{nickname}),'%')
and date(t_wx_user.UPDATE_TIME) BETWEEN #{startTime} and #{endTime}

SELECT ID
FROM t_wx_user u
WHERE u.USER_ID = #{userId}
